import React from 'react'
import { useState } from 'react'
import Axios  from 'axios'
import './card.css'
const pro=[
    {
        _id:7,
        title:"Adisa bag",
        brand:"ADISA",
        image:"data:image/jpeg;base64,",
        price:2000,
        count:0
    },
    {
        _id:8,
        title:"Skybags",
        brand:"SKYBAGS",
        image:"data:image/jpeg;base64,",
        price:1500,
        count:0
    },
    {
        _id:9,
        title:"Aircraft bags",
        image:"data:image/jpeg;base64,",
        brand:"AIRCRAFT",
        price:1000,
        count:0
    }
]
export default function Bags() {
    const [quan, setQuan] = useState(Array(pro.length).fill(0));
    const inc = (index) => {
        setQuan(prevQuan => {
            const newQuan = [...prevQuan];
            newQuan[index]++;
            return newQuan;
        });
    };

    const dec = (index) => {
        setQuan(prevQuan => {
            const newQuan = [...prevQuan];
            if (newQuan[index] > 0) {
                newQuan[index]--;
            }
            return newQuan;
        });
    };
    const fun = (item, index) => {
        if (quan[index] > 0) {
           // localStorage.setItem(item._id, JSON.stringify(item));
            Axios.post('http://localhost:4000/get', { id: item._id })
                .then((res) => {
                    let ack=res.data;
                    if (ack ==="found") {
                        item.count += quan[index];
                        Axios.post('http://localhost:4000/update', { id:item._id,count:item.count})
                            .then((res2) => {
                                let ack2=res2.data;
                                if (ack2==="success") {
                                    alert("Updated in cart");
                                } else {
                                    alert("Error in updation");
                                }
                            })
                            .catch((err2) => {
                                alert("Error in update")
                                alert(err2);
                            });
                    } else{
                        item.count=quan[index];
                        Axios.post('http://localhost:4000/ins', { fdata: item })
                            .then((res) => {
                                let ack3=res.data;
                                console.log("Insert");
                                if (ack3 === "success") {
                                    alert('Added to Cart');
                                } else {
                                    alert('Not Added to Cart');
                                }
                            })
                            .catch((error) => {
                                alert("Error in insert");
                                alert(error);
                            });
                        item.count=0;
                    }
                })
                .catch((err3) => {
                    alert("Error in get");
                    alert(err3);
                });

            setQuan((prevQuan) => {
                const newQuan = [...prevQuan];
                newQuan[index] = 0;
                return newQuan;
            });
        }
    };

       return (
        <>
            <h2>HOME</h2>
            <div className='box-1'>
                {pro.map((item, index) => (
                    <div className='category' key={item._id}>
                        <h3>{item.title}</h3>
                        <img src={item.image} alt='img' />
                        <h3>{item.brand}</h3>
                        <h4>â‚¹{item.price}</h4>
                        <button className='btn-card' onClick={() => fun(item, index)}>Add to Cart</button>
                        <br></br>
                        <button className='btn-card' onClick={() => inc(index)}>+</button>
                        <span>{quan[index]}</span>
                        <button className='btn-card' onClick={() => dec(index)}>-</button>
                    </div>
                ))}
            </div>
        </>
    );
}

